# Carregar items do JSON existente
with open('/home/user/plenya/lab_items_enrichment_data.json') as f:
    existing = json.load(f)

ALL_ITEMS = existing + MORE_ITEMS

success = 0
errors = []

print("=" * 80)
print("PROCESSANDO 20 SCORE ITEMS DE EXAMES")
print("=" * 80)

for i, item in enumerate(ALL_ITEMS, 1):
    print(f"\n[{i}/20] {item['name']}...", end=" ")

    payload = {
        "clinicalRelevance": item["clinicalRelevance"],
        "patientExplanation": item["patientExplanation"],
        "conduct": item["conduct"]
    }

    try:
        r = requests.put(f"{API}/score-items/{item['id']}", headers=H, json=payload, timeout=10)
        if r.status_code in [200, 201]:
            print("✓ SUCESSO")
            success += 1
        else:
            print(f"✗ ERRO {r.status_code}")
            errors.append({"item": item["name"], "status": r.status_code, "msg": r.text[:100]})
    except Exception as e:
        print(f"✗ EXCEÇÃO: {str(e)[:50]}")
        errors.append({"item": item["name"], "error": str(e)[:100]})

    time.sleep(0.3)

print("\n" + "=" * 80)
print(f"RESUMO: {success}/20 sucessos, {len(errors)} erros")
if errors:
    print("\nERROS:")
    for e in errors:
        print(f"  - {e}")
print("=" * 80)
