INSERT INTO article_score_items (article_id, score_item_id)
SELECT a.id, s.id
FROM articles a
CROSS JOIN (
  SELECT id FROM score_items WHERE id IN (
    'b8f0bfbd-ce5d-4de3-aff2-96dceb4a9b68',
    '33d83614-491e-440c-adb9-553cee48ed76',
    'bda631d4-0a5d-42d0-bc41-2d9516ec2bef',
    'a16f11ad-308f-4049-a76a-7aee90cce76c',
    '7a182c48-8b53-49e7-baeb-71f282325420',
    '0c052bec-e860-4068-927c-db125905c0d3',
    '76215a40-32b2-4df7-86ff-3b424df41f1a',
    '25b3da57-83df-4673-b0c8-914fa21e6f1e',
    '3f6fb4d7-efa4-4dac-93ef-95da8fbee038',
    '10655c96-4de2-4c05-9ab4-7ab5fcf1cefb',
    '3be39560-d2f1-424c-9f1d-bfbd9472558a',
    'ac440fb9-7452-40c3-b896-a0d50a9e3054',
    '2e9e9628-b7d3-49b6-ae60-61aca75de8f8',
    '5579c87a-f1b7-46c9-88ab-5edbc63a9f5f'
  )
) s
WHERE a.title ILIKE '%bioquímica%'
   OR a.title ILIKE '%fisiologia%'
