SELECT
    si.name,
    CASE
        WHEN si.clinical_relevance IS NOT NULL AND si.patient_explanation IS NOT NULL AND si.conduct IS NOT NULL
        THEN '✅ COMPLETO'
        ELSE '❌ INCOMPLETO'
    END as status,
    LENGTH(si.clinical_relevance) as len_clinical,
    LENGTH(si.patient_explanation) as len_patient,
    LENGTH(si.conduct) as len_conduct,
    si.last_review
FROM score_items si
WHERE si.id IN (
